mapper.update:
update member_device_info
set status=#{state}
where device_sn = #{deviceSn}

update member_device_info
set room_id=#{roomId}
where device_id = #{deviceId}

mapper.delete:
update member_device_info
set room_id=null,store_id=null
where store_id=#{id}

mapper.select:
select device_sn
from member_device_info
where deleted = 0
  AND type = #{type}
  AND room_id is null
  AND store_id = #{storeId}

select d.*, s.store_name, r.room_name
from member_device_info d
left join member_store_info s ON s.store_id = d.store_id
left join member_room_info r ON r.room_id = d.room_id
where d.deleted = 0


        and locate(#{reqVO.deviceSn},d.device_sn)>0


        and d.type=#{reqVO.type}


        and d.status=#{reqVO.status}


        and d.share=#{reqVO.share}


        and d.room_id=#{reqVO.roomId}


        and d.store_id=#{reqVO.storeId}


        and d.store_id IN (select store_id from member_store_user where deleted = 0 and user_id = #{userId} AND
        type IN (12,13))


        and d.create_time between

            #{reqVO.dateTime}



order by d.create_time desc

select count(1)
from member_device_info
where deleted = 0
  and type = #{deviceType}
  and room_id = #{roomId}
