mapper.select:
select d.*, u.nickname,u.mobile,s.store_name, r.room_name
from member_device_use_info d
left join member_user u ON u.id = d.user_id
left join member_store_info s ON s.store_id = d.store_id
left join member_room_info r ON r.room_id = d.room_id
where d.deleted = 0


        AND locate(#{reqVO.cmd},d.cmd)>0


        AND locate(#{reqVO.nickname},u.nickname)>0


        AND d.store_id=#{reqVO.storeId}


        AND d.room_id=#{reqVO.roomId}


        AND d.create_time>=#{reqVO.createTime[0]}
        AND d.create_time<=#{reqVO.createTime[1]}


order by d.create_time desc
