mapper.update:
update
    member_discount_rules
set status=#{status}
where discount_id = #{id}

mapper.select:
select dr.*, s.store_name from member_discount_rules dr left join member_store_info s ON s.store_id = dr.store_id left join member_store_user su ON s.store_id = su.store_id and su.deleted=0 where dr.deleted = 0 and s.deleted=0 and su.user_id=#{reqVO.userId} and su.type in (12,13)
and dr.store_id=#{reqVO.storeId}
order by pay_money

select count(1)
from member_discount_rules
where deleted = 0
AND store_id = #{storeId}
AND pay_money = #{payMoney}

    and discount_id!=#{discountId}
