mapper.update:
update member_face_record
set type=#{type},admit_guid=#{guid}
where id = #{id}

update member_face_record
set admit_guid='STRANGERBABY'
where admit_guid = #{admitGuid}

mapper.select:
select f.*, store.store_name,store.store_id
from member_face_record f
left join member_store_info store ON f.store_id = store.store_id
where f.deleted = 0


        and f.store_id=#{reqVO.storeId}


        and f.type=#{reqVO.type}


        and locate(#{reqVO.deviceSn},device.device_sn)>0


        and locate(#{reqVO.admitGuid},f.admit_guid)>0


        and f.store_id IN (select store_id from member_store_user where deleted = 0 and user_id = #{userId} AND
        type IN (12,13))


        AND f.show_time>=#{reqVO.showTime[0]}
        AND f.show_time<=#{reqVO.showTime[1]}


order by f.create_time desc
