mapper.select:
select ifnull(sum(group_pay_price),0)
from member_group_pay_info where deleted=0
AND store_id IN

    #{item}


    AND store_id=#{storeId}

select g.*, s.store_name from member_group_pay_info g left join member_store_info s ON g.store_id = s.store_id where g.deleted = 0
and locate(#{reqVO.groupName},g.group_name)>0
and locate(#{reqVO.groupNo},g.group_no)>0
and g.group_pay_type=#{reqVO.groupPayType}
and g.store_id=#{reqVO.storeId}

select ifnull(sum(group_pay_price),0)
from member_group_pay_info where deleted=0 and group_pay_type IN (1,3)
AND store_id IN

    #{item}


    AND store_id=#{storeId}

select ifnull(sum(group_pay_price),0)
from member_group_pay_info where deleted=0 and group_pay_type=2
AND store_id IN

    #{item}


    AND store_id=#{storeId}

select ifnull(sum(group_pay_price), 0) tgMoney from member_group_pay_info where deleted = 0 and group_pay_type =#{groupPayType}
and to_days(create_time) >= to_days(#{startTime}) and to_days(create_time) <= to_days(#{endTime})
and store_id=#{storeId}
and store_id in (select store_id from member_store_user where deleted = 0 and user_id = #{userId} AND type IN (12,13))
