mapper.update:
update member_store_info
set expire_time=#{newDate}

    ,status=#{status}

where deleted=0 and store_id=#{storeId}

mapper.select:
select *
,( 6371 * ACOS( COS(RADIANS(#{lat})) * COS(RADIANS(lat)) * COS(RADIANS(lon) - RADIANS(#{lon})) + SIN(RADIANS(#{lat})) * SIN(RADIANS(lat)) ) ) AS distance
from member_store_info where deleted = 0 and status = 0 and store_id = #{storeId}

select r.*,s.clear_time
from member_room_info r
left join member_store_info s ON r.store_id=s.store_id
where r.deleted = 0
  and r.store_id = #{storeId}

    and r.room_class=#{roomClass}


    and r.status=#{status}

order by r.status , r.sort_id asc

SELECT s.*,device.device_data lockData, ( SELECT count( 1 ) FROM member_room_info WHERE deleted = 0 AND store_id = s.store_id ) roomNum, ( SELECT count( 1 ) FROM member_device_info WHERE deleted = 0 AND store_id = s.store_id ) deviceNum, ( SELECT count( 1 ) FROM member_device_info WHERE deleted = 0 AND STATUS = 1 AND store_id = s.store_id ) deviceOnlineNum FROM member_store_info s left join member_store_user su on su.store_id=s.store_id and su.deleted=0 left join member_device_info device ON device.store_id = s.store_id and device.room_id is null and device.type = 5 and device.deleted = 0 WHERE s.deleted = 0 and su.user_id=#{reqVO.userId} and su.type in (12,13)
and locate(#{reqVO.name},s.store_name)>0
and s.city_name=#{reqVO.cityName}

select order_door_open
from member_store_info
where deleted = 0 AND store_id=#{storeId}
